class JointureFamilleRepository extends EntityRepository
{
	public function FamilleUserFindAll()
	{
		// MÃ©thode 1 : en passant par l'EntityManager
    $qb = $this->createQueryBuilder('a');
  	$qb
    ->leftJoin('a.famille', 'b')
  ;



  return $qb
    ->getQuery()
    ->getResult()
  ;
	}





}
